mapper.select:
select from tbUserInfo
valid = #{valid,jdbcType=BIT}
and ( userAccount like concat('%',#{keyword,jdbcType=VARCHAR},'%') or cellPhone like concat('%',#{keyword,jdbcType=VARCHAR},'%') or nickName like concat('%',#{keyword,jdbcType=VARCHAR},'%') or realName like concat('%',#{keyword,jdbcType=VARCHAR},'%') or organizationname like concat('%',#{keyword,jdbcType=VARCHAR},'%') )
